class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: props.text_list,
      color_flag: [],
      flag: true,
      color_flag: Array(props.text_list.length).fill(false),
    };
  }

  render() {
    return (
      <div>
        <div className="word_list">
          <ul>
            {this.state.text.map((value) => (
              <li className="li_button">
                <div className="li_label">{value}</div>
                <CheckBox />
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
