function App(props) {
  const [text, setText] = useState(props.text_list),
    flag = true,
    color_flag = Array(props.text_list.length).fill(false);

  return (
    <div>
      <div className="word_list">
        <ol className="ol_label">
          {text.map((value) => (
            <li>
              <div>{value}</div>
              <CheckBox />
            </li>
          ))}
        </ol>
      </div>
    </div>
  );
}

export default App;
