import React, {Component} from "react";
import firebase from "firebase/app";
import "./App.css";

// const firebaseConfig = {
//   apiKey: "AIzaSyA_Bx8VfyUxO1twJQrZ9-u37fcNF3lwKrI",
//   authDomain: "word-test-916dc.firebaseapp.com",
//   projectId: "word-test-916dc",
//   storageBucket: "word-test-916dc.appspot.com",
//   messagingSenderId: "482569027636",
//   appId: "1:482569027636:web:393808c6c616ab7f0e4a61",
// };

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      text: ["test", "red", "value", "blue", "word"],
      flag: true,
      color_flag: [],
    };
    this.colorChange = this.colorChange.bind(this);
    this.initial_value = this.initial_value.bind(this);
  }

  initial_value(event) {
    if (this.state.flag == true) {
      this.state = {
        color_flag: Array(this.state.text.length).fill(true),
        flag: false,
      };
    }
    this.colorChange(event);
  }

  colorChange(event) {
    let li_list = document.getElementsByClassName("li_label");
    console.log(this.state.color_flag);

    if (this.state.color_flag[0] == true) {
      li_list[0].style.color = "blue";
      this.state.color_flag[0] = false;
    } else {
      li_list[0].style.color = "red";
      this.state.color_flag[0] = false;
    }
  }

  render() {
    return (
      <div>
        <div className="word_list">
          <ul>
            {this.state.text.map((value) => (
              <li className="li_button">
                <div className="li_label">{value}</div>
                <label className="check_box">
                  <input
                    type="checkbox"
                    value={this.state.value}
                    onChange={this.initial_value}
                  />
                </label>
              </li>
            ))}
          </ul>
        </div>
      </div>
    );
  }
}

export default App;
